package com.example.uptop.pandorikatest.rest;import com.example.uptop.pandorikatest.rest.api.RecordingsApi;import com.example.uptop.pandorikatest.util.ConstantManager;import retrofit.RestAdapter;public class RestClient {    private RecordingsApi recordingsApi;    public RestClient() {        RestAdapter restAdapter = new RestAdapter.Builder()        .setEndpoint(ConstantManager.BASE_URL)        .setLogLevel(RestAdapter.LogLevel.FULL)        .build();        recordingsApi = restAdapter.create(RecordingsApi.class);    }    public RecordingsApi getBashImApi() {        return recordingsApi;    }}